import logo from './logo.svg';
import './App.css';
import Account from './Components/Account';
import Web3 from 'web3';
import React, {useState, useEffect} from 'react';
import contractABI from './ABI.json'  // Solidity contract ABI

function App() {

  // declare hook for setting the defaultAccount. App will re-render after calling setDefaultAccount(myNewDefaultAccount)
  const [defaultAccount, setDefaultAccount] = useState(null);

  const [web3, setWeb3] = useState(null);

  // get the user account
  function loadWeb3() {
  //const PLeb3 = setWeb3(new Web3(Web3.currentProvider)) || setWeb3(new Web3.providers.WebsocketProvider("ws://localhost:8545"));

  if(typeof web3 !== 'undefined'){
    setWeb3(new Web3(Web3.currentProvider));
  } else {
    setWeb3(new Web3(new Web3.providers.WebsocketProvider('ws://localhost:8545')));
  }

  //var RPSContract = new web3.eth.Contract(contractABI, '0x292e75fe7450dD6E011368437663AF463FEeedd3');

  // web3.eth.getAccounts()
  // .then(function(result){
  //   setDefaultAccount(result[0]);
  //   //console.log('Default Account: ' + defaultAccount);
  // });
}

function loadAccounts(){
  web3.eth.getAccounts()
  .then(function(result){
    setDefaultAccount(result[0]);
  });
}

// React Hook that runs first time app is rendered
useEffect(() => {
  loadWeb3();

}, []); 

// React Hook that runs on app render and when web3 is updated with setWeb3()
useEffect(() => {
  loadAccounts();
}, [web3]);


return (
<div>
<Account account={defaultAccount}/>
</div>
  );
}

export default App;
