function Attack(props) {

	function AttackClickHandler(attack){
    console.log('Attack: ' + attack);
    console.log(props.contract);
    props.contract.methods.play(attack).send({from: props.defaultAccount});
  }

	return (
	<div>
	<button value={props.attackNum} onClick={e => {AttackClickHandler(props.attackNum)}}> Play {props.hand} </button>
	<input type='image' onClick={e => {AttackClickHandler(props.attackNum)}} src='Rock_img.png'/>
	</div>
	);
}

export default Attack;
