import './Donate.css'

function Donate(props) {

	function SendDonation() {
		console.log('Donated!');
		props.contract.methods.Donate().send({from: props.defaultAccount});
	}

	return (
	<div className='DonateCard'> 
	<p> Contract Balance: </p>
	<p>{props.contractBalance}</p>

	<button onClick={SendDonation}> Donate to the faucet </button>
	</div>
	)
}

export default Donate;
