import logo from './logo.svg';
import './App.css';
import Account from './Components/Account';
import Web3 from 'web3';
import React, {useState, useEffect} from 'react';
import contractABI from './ABI'

function App() {

  // declare hook for setting the defaultAccount. App will re-render after calling setDefaultAccount(myNewDefaultAccount)
  const [defaultAccount, setDefaultAccount] = useState(null);

  // get the user account
  function loadBlockchainData() {
  const web3 = new Web3(Web3.currentProvider || new Web3.providers.WebsocketProvider("ws://localhost:8545"));

  web3.eth.getAccounts()
  .then(function(result){
    setDefaultAccount(result[0]);
    //console.log('Default Account: ' + defaultAccount);
  })


}

// React Hook that runs everytime app is rendered and when defaultAccount is updated
useEffect(() => {
  loadBlockchainData();
  console.log(contractABI.json);
}, [defaultAccount]);


return (
<div>
<Account account={defaultAccount}/>
</div>
  );
}

export default App;
