	let playerAttack;
	let computerAttack;

	console.log(props.playerAttack);

	function convertAttackToString(attackNum){
	switch(attackNum) {
		case 0:
			return 'Rock';
			break;
		case 1:
			return 'Paper';
			break;
		case 2:
			return 'Scissors';
			break;
		default:
		return 'Error';
		}

	}

	return (
		<div> 
		<div>Match Outcome: {props.outcome} </div>
		<div> Player threw: {} </div>
		</div>
		);
}

export default Outcome;
