import logo from './logo.svg';
import './App.css';
import Account from './Components/Account';
import Web3 from 'web3';
import React, {useEffect} from 'react';

function App() {

  // get the 
  function loadBlockchainData() {
  const web3 = new Web3(Web3.currentProvider || new Web3.providers.WebsocketProvider("ws://localhost:8545"));

  web3.eth.getAccounts()
  .then(function(result){
    web3.eth.defaultAccount = result[0];
    console.log('Default Account: ' + web3.eth.defaultAccount);
  })


}

// React Hook that runs everytime app is rendered.
useEffect(() => {
  loadBlockchainData();
});


  return (
<div>
<Account account={web3.eth.defaultAccount}/>
</div>
  );
}

export default App;
