//import './node_modules/web3/dist/web3.min.js';

function Account(props) {

		// if there is no web3 injected (MetaMask) then look at localhost for web3 (ganache)
		if(typeof web3 !== 'undefined'){
			web3 = new Web3(web3.currentProvider);
		} else {
			// IMPORTANT!!
			// USED WebsocketProvider instead of httpProvider due to event listening only happening on ws
			web3 = new Web3(new Web3.providers.WebsocketProvider('ws://localhost:8545'));
		}

	return (
		<div> Account is here! </div>
		);
}

export default Account;
