import Rock_img from './Rock_img.png'
import Paper_img from './Paper_img.png'
import Scissors_img from './Scissors_img.png'
import './Outcome.css'

function Outcome(props) {

	let playerAttack = convertAttackToString(props.playerAttack);
	let computerAttack = convertAttackToString(props.computerAttack);

	function convertAttackToString(attackNum){
	if (attackNum != null) {
	switch(attackNum) {
		case '0':
			return 'Rock';
			break;
		case '1':
			return 'Paper';
			break;
		case '2':
			return 'Scissors';
			break;
		default:
		return 'Error';
		}
	}
	}

	  // Set the image for the 'button' based on the attack number
function ReturnImageToDisplay(num){
	let imageToDisplay;
  switch(num){
  	case '0':
  	imageToDisplay = Rock_img;
  	break;
  	case '1':
  	imageToDisplay = Paper_img;
  	break;
  	case '2':
  	imageToDisplay = Scissors_img;
  	break;
  }
  return imageToDisplay;
}

	return (
		<div className='OutcomeCard'>
		<div className='Hand'>
		<h3> Player </h3>
		<img src={ReturnImageToDisplay(props.playerAttack)}/>
		</div>
		<div className='Outcome'> <h3>Match Outcome </h3>
		<div> <h3>{props.outcome}</h3> </div>
		</div>
		<div className='Hand'>
		<h3> Ethereum VM </h3>
		<img src={ReturnImageToDisplay(props.computerAttack)}/>
		</div>
		</div>
		);
}

export default Outcome;
